mapper.update:
UPDATE relations
SET relation_label_id = #{relationLabelId}, head_entity_id = #{headEntityId}, tail_entity_id = #{tailEntityId}
WHERE id = #{id};

mapper.select:
SELECT * FROM relations
WHERE document_id = #{documentId}
  AND relation_label_id = #{relationLabelId}
  AND head_entity_id = #{headEntityId}
  AND tail_entity_id = #{tailEntityId}
